import { createTheme } from '@mui/material/styles';

const telegramTheme = window.Telegram?.WebApp?.themeParams || {
  bg_color: '#000000',
  text_color: '#ffffff',
  hint_color: '#999999',
  button_color: '#00ff00',
  button_text_color: '#ffffff',
};

export const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: telegramTheme.button_color,
    },
    background: {
      default: telegramTheme.bg_color,
      paper: telegramTheme.bg_color,
    },
    text: {
      primary: telegramTheme.text_color,
      secondary: telegramTheme.hint_color,
      primary: '#ffffff',
      secondary: '#999999',
    },
  },
  typography: {
    fontFamily: '"Roboto Mono", monospace',
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          textTransform: 'none',
        },
      },
    },
  },
});
